package JCS;

public class CounterLogic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num =18;
int count=0;
for(int i=1;i<=num;i++)
{
	if(num%i==0)
	{
		System.out.println(i);//this is to print the factor itself
		count=count+1;
	}
	
}
{
	System.out.println("count"+count);// this is to print count of factor
}
	}

}
